(1, '{\"es\":\"Necesaria\"}', 'necessary', '{\"es\":\"<p>Las cookies necesarias son cruciales para las funciones b\\u00e1sicas del sitio web y el sitio web no funcionar\\u00e1 de la forma prevista sin ellas. Estas cookies no almacenan ning\\u00fan dato de identificaci\\u00f3n personal.<\\/p>\"}', 1, 1, 0, 1, '[]', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(2, '{\"es\":\"Funcional\"}', 'functional', '{\"es\":\"<p>Las cookies funcionales ayudan a realizar ciertas funcionalidades, como compartir el contenido del sitio web en plataformas de redes sociales, recopilar comentarios y otras caracter\\u00edsticas de terceros.<\\/p>\"}', 0, 1, 0, 1, '[]', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(3, '{\"es\":\"Anal\\u00edtica\"}', 'analytics', '{\"es\":\"<p>Las cookies anal\\u00edticas se utilizan para comprender c\\u00f3mo interact\\u00faan los visitantes con el sitio web. Estas cookies ayudan a proporcionar informaci\\u00f3n sobre m\\u00e9tricas el n\\u00famero de visitantes, el porcentaje de rebote, la fuente de tr\\u00e1fico, etc.<\\/p>\"}', 0, 1, 0, 1, '[]', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(4, '{\"es\":\"El rendimiento\"}', 'performance', '{\"es\":\"<p>Las cookies de rendimiento se utilizan para comprender y analizar los \\u00edndices de rendimiento clave del sitio web, lo que ayuda a proporcionar una mejor experiencia de usuario para los visitantes.<\\/p>\"}', 0, 1, 0, 1, '[]', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(5, '{\"es\":\"Anuncio\"}', 'advertisement', '{\"es\":\"<p>Las cookies publicitarias se utilizan para entregar a los visitantes anuncios personalizados basados \\u200b\\u200ben las p\\u00e1ginas que visitaron antes y analizar la efectividad de la campa\\u00f1a publicitaria.<\\/p>\"}', 0, 1, 0, 1, '[]', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
